package example02;

public class Var {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//정수
		int a;
		a = 1;
		System.out.println(a+1); //2
		 
		a = 2;
		System.out.println(a+1); //3
		
		//실수
		double b = 1.1; 
		System.out.println(b+1.1); // 2.2
		 
		b = 2.1; 
		System.out.println(b+1.1); // 3.2
		
	}

}
